import { useEffect, useState } from 'react';
import { Nav, Navbar, Container, Form, Button, FormControl} from 'react-bootstrap'
import MovieCard from './MovieCard';

let api_key = "&api_key=9a12f7bf2a75b481ed2612d8faf63a35";
let baseUrl = "https://api.themoviedb.org/3";
let url = baseUrl+"/discover/movie?primary_release_date.gte=2014-09-15&primary_release_date.lte=2014-10-22"+api_key;


const Main = ()=>{
    const [ movieData, setData] = useState([]);
    const [ url_set, setUrl ] = useState(url);

    useEffect(()=>{
        fetch(url_set).then(res=>res.json()).then(data=>{
            //console.log(data);
            setData( data );
        });
    }, [url_set])
    return (
        <>
            <div className='header'>
            <Navbar bg="light" expand="lg">
                <Container fluid>
                    {/* <Navbar.Brand href="#">Navbar scroll</Navbar.Brand> */}
                    <Navbar.Toggle aria-controls="navbarScroll" />
                    <Navbar.Collapse id="navbarScroll">
                        <Nav
                            className="me-auto my-2 my-lg-0"
                            style={{ maxHeight: '100px' }}
                            navbarScroll
                        >
                           <Nav.Link href="#home">Movies in theater</Nav.Link>
                            <Nav.Link href="#link">Coming soon</Nav.Link>
                            <Nav.Link href="#link">Top rated movies</Nav.Link>
                            <Nav.Link href="#link">Kids</Nav.Link>
                            <Nav.Link href="#link">Favorites</Nav.Link>

                        </Nav>
                        <Form className="d-flex">
                            <FormControl
                                type="search"
                                placeholder="Search"
                                className="me-2"
                                aria-label="Search"
                            />
                            <Button variant="outline-success">Search</Button> 
                        </Form>
                    </Navbar.Collapse>
                </Container>
                </Navbar>
            </div>
            <h2 className='mx-2'>Movies</h2>
            <div className='container'>
                {
                    (movieData.length===0)?<p className='notfound'>Not Found</p>:movieData.map((res, pos)=>{
                        return(
                            <MovieCard info={res} key={pos}/>
                        )
                    })

                }
            </div>

        </>
    )
}

export default Main;
