import {Card, Button, Col, Badge} from 'react-bootstrap'
import { Link } from 'react-router-dom';

const MovieCard = (movie) =>{
    //console.log(movie.info)
    let img_path="https://image.tmdb.org/t/p/w500";
    return (
        <div>

                <Col key={movie.info.id} className='d-flex align-items-stretch my-3'>
                    <Card style={{ width: '18rem' }}>
                        <Card.Img variant="top" src={img_path+movie.info.poster_path} />
                            <Card.Body>
                                <Card.Title >{movie.info.title}
                                    <Badge pill bg="info" className='mx-4'>
                                          { movie.info.vote_average }   
                                    </Badge>{' '}
                                </Card.Title>
                                {/* <a href="https://api.themoviedb.org/3/movie/550?api_key=9a12f7bf2a75b481ed2612d8faf63a35" 
                                className="stretched-link">
                                </a> */}
                                <div className='d-flex justify-content-between'>
                                    <Button variant="outline-info">Add to favorite</Button>
                                    <Link>
                                        <Button variant="outline-info" className='mx-3'>More</Button>
                                    </Link>

                                </div>  
                            </Card.Body>
                        </Card>
                </Col>
        </div>
    )
}

export default MovieCard
