import { useParams } from "react-router-dom";
import { useEffect, useState } from 'react';
import { Card } from 'react-bootstrap'

let api_key = "?api_key=9a12f7bf2a75b481ed2612d8faf63a35";
let baseUrl = "https://api.themoviedb.org/3";
let img_path="https://image.tmdb.org/t/p/w500";



const Info = () =>{
    const params = useParams();
    let id = params.id;
    let url = baseUrl+"/movie/"+id+api_key;

    const [ movie, setMovie] = useState(url);


    useEffect(()=>{
        fetch(url).then(res=>res.json()).then(data=>{
            setMovie( data);
            //console.log("Ran fetch");
        });
    }, [url])


    return (
        <div>
            Movie details {id}
            Moveie runtime {movie.vote_count}
            <Card className="bg-dark text-white">
                <Card.Img src={img_path + movie.poster } alt="Card image" />
                <Card.ImgOverlay>
                    <Card.Title>Card title</Card.Title>
                    <Card.Text>
                        {movie.overview}
                    </Card.Text>
                    <Card.Text>Last updated 3 mins ago</Card.Text>
                </Card.ImgOverlay>
            </Card>
        </div>
    )
}

export default Info
